import { v4 as uuidv4 } from "uuid";
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export default createSlice({
  name: "users",
  initialState: [
    {
      id: 1,
      username: "anchay",
      liked: [],
      password: "avc",
      diet: null,
    },
    {
      id: 2,
      username: "toitenho",
      diet: null,
      liked: [],
      password: "123",
    },
    {
      id: 3,
      username: "ranhmanh",
      liked: [],
      password: "1234",
      diet: null,
    },
    {
      username: "toitenlatho",
      liked: [],
      password: "ddv",
      diet: "null",
      id: 4,
    },
    {
      username: "toikhongdu",
      liked: [],
      password: "ranhmanh",
      diet: "null",
      id: 5,
    },
    {
      username: "anman",
      liked: [],
      password: "012",
      diet: "null",
      id: 6,
    },
    {
      username: "ancutgi",
      password: "cuchinh",
      liked: [],
      diet: "null",
      id: 7,
    },
    {
      username: "djtcu",
      password: "111",
      diet: "null",
      liked: [],
      id: 8,
    },
    {
      username: "anho",
      password: "anho",
      diet: "null",
      liked: [],
      id: 9,
    },
  ],
  reducers: {
    updatePasswordChange: (state, action) => {
      const currentUser = state.find((user) => user.id === action.payload.id);
      currentUser.password = action.payload.newPassword;
    },
    addUserChange: (state, action) => {
      state.push({
        username: action.payload.username,
        password: action.payload.password,
        diet: "null",
        liked: [],
        id: uuidv4(),
      });
    },
    toggleUserLikeChange: (state, action) => {
      const currentUser = state.find(
        (user) => user.id === action.payload.userId
      );
      if (currentUser) {
        if (!currentUser.liked.includes(action.payload.foodId)) {
          currentUser.liked.push(action.payload.foodId);
        } else {
          currentUser.liked = currentUser.liked.filter(
            (foodId) => foodId !== action.payload.foodId
          );
        }
      }
    },
  },
});

export const fetchUsers = createAsyncThunk("users/fetchUsers", async () => {
  const res = await fetch("https://jsonplaceholder.typicode.com/photos");
  const data = JSON.parse(res);
  console.log("FETCH PHOTOS", data);
});
