import { Link } from 'react-router-dom';
import { useState } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft } from '@fortawesome/free-solid-svg-icons';

import classNames from 'classnames/bind';
import styles from './Login.module.scss';
const cx = classNames.bind(styles);

function Login() {
    const [user, setUser] = useState({
        username: '',
        password: '',
    });

    const handleChangeUsername = (e) => {
        setUser({ ...user, username: e.target.value });
    };
    return (
        <div class={cx('wrapper')}>
            <div class={cx('fadeIn first')}>
                <img
                    class={cx('header')}
                    src="https://media.wired.co.uk/photos/626c4ae5c7aeb6a48f3c9c68/3:2/w_2025,h_1350,c_limit/Spirited-Away-Studio-Ghibli-Ranking-MMDSPAW_EC001.jpg"
                    alt=""
                />
            </div>

            <h4 class={cx('label')}>
                <Link to="/">
                    <FontAwesomeIcon className={cx('back-icon')} icon={faChevronLeft} />
                </Link>
                Đăng nhập
            </h4>

            <form class={cx('form-template')}>
                <input
                    class={cx('fadeIn second')}
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={user.username}
                    onChange={handleChangeUsername}
                />
                <input type="password" class={cx('fadeIn third')} placeholder="Mật khẩu" />
                <input type="submit" class={cx('fadeIn fourth')} value="Đăng nhập" />
            </form>

            <Link class={cx('forgot-password')} to="/forgotPassword">
                Quên mật khẩu?
            </Link>

            <div className={cx('formFooter')}>
                Chưa có tài khoản?
                <Link to="/register" class={cx('underlineHover')}>
                    Đăng ký
                </Link>
                ngay
            </div>
        </div>
    );
}

export default Login;
