import { createSelector } from "reselect";

export const foodsSelector = (state) => state.foods;

export const searchNameSelector = (state) => state.filters.name;

export const foodsRemainingSelector = createSelector(
  foodsSelector,
  searchNameSelector,
  (foods, name) => {
    if (Array.isArray(foods))
      return foods.filters((food) => {
        return food.name.includes(name);
      });
  }
);
