export const searchNameChange = (text) => ({
  type: "filters/searchNameChange",
  payload: text,
});

export const searchDietChange = (value) => ({
  type: "filters/searchDietChange",
  payload: value,
});

export const searchIngredientsChange = (value) => ({
  type: "filters/searchIngredientsChange",
  payload: value,
});

export const updatePasswordChange = ({ newPassword, id }) => ({
  type: "users/updatePasswordChange",
  payload: { newPassword, id },
});

export const addUserChange = ({ username, password }) => ({
  type: "users/addUserChange",
  payload: { username, password },
});

export const addFoodLikeChange = () => ({
  type: "foods/addFoodLikeChange",
  payload: null,
});
