import { FoodCard } from "./FoodCard";
import { useEffect, useState } from "react";

import classNames from "classnames/bind";
import styles from "./Main.module.scss";
import { FoodDetails } from "./FoodDetails";
const cx = classNames.bind(styles);

function Main({ foodSearch }) {
  const [isShowDetails, setIsShowDetails] = useState(false);

  const handleShowDetails = () => {
    setIsShowDetails(!isShowDetails);
  };

  const handleHideDetails = () => {
    setIsShowDetails(!isShowDetails);
  };

  const foods = [
    {
      id: 1,
      name: "Kem dừa mã lai",
      list: ["sữa", "sữa chua", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Làm đông 30p", "Rắc dừa khô"],
      heart: 30,
    },
    {
      id: 2,
      name: "Kem matcha mã lai",
      list: ["sữa", "bột matcha", "dừa", "dừa khô"],
      info: [
        "Xay sữa dừa matcha",
        "Trộn bột matcha",
        "Làm đông 30p",
        "Rắc dừa khô",
      ],
      heart: 40,
    },
    {
      id: 3,
      name: "Kem xoài mã lai",
      list: ["sữa", "xoài", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Xay xoài", "Làm đông 30p", "Rắc dừa khô"],
      heart: 60,
    },
    {
      id: 4,
      name: "Kem bơ mã lai",
      list: ["sữa", "bơ", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Xay bơ", "Làm đông 30p", "Rắc dừa khô"],
      heart: 50,
    },
  ];
  return (
    <div className={cx("wrapper")}>
      {!isShowDetails &&
        foods.map((food, index) => (
          <FoodCard
            key={index}
            name={food.name}
            list={food.list}
            info={food.info}
            heart={food.heart}
            onClick={() =>
              handleShowDetails({
                name: food.name,
                list: food.list,
                info: food.info,
                heart: food.heart,
              })
            }
          />
        ))}
      {isShowDetails && <FoodDetails onClick={handleHideDetails} />}
    </div>
  );
}

export default Main;
