import { uuid as uuidv4 } from "uuid";

const initState = [
  {
    id: 1,
    username: "anchay",
    liked: [],
    password: "avc",
    diet: null,
  },
  {
    id: 2,
    username: "toitenho",
    diet: null,
    liked: [],
    password: "123",
  },
  {
    id: 3,
    username: "ranhmanh",
    liked: [],
    password: "1234",
    diet: null,
  },
  {
    username: "toitenlatho",
    liked: [],
    password: "ddv",
    diet: "null",
    id: 4,
  },
  {
    username: "toikhongdu",
    liked: [],
    password: "ranhmanh",
    diet: "null",
    id: 5,
  },
  {
    username: "anman",
    liked: [],
    password: "012",
    diet: "null",
    id: 6,
  },
  {
    username: "ancutgi",
    password: "cuchinh",
    liked: [],
    diet: "null",
    id: 7,
  },
  {
    username: "djtcu",
    password: "111",
    diet: "null",
    liked: [],
    id: 8,
  },
  {
    username: "anho",
    password: "anho",
    diet: "null",
    liked: [],
    id: 9,
  },
];

const usersReducer = (state = initState, action) => {
  switch (action.type) {
    case "users/updatePasswordChange":
      return state.map((user) =>
        user.id === action.payload.id
          ? { ...user, password: action.payload.newPassword }
          : user
      );
    case "users/addUserChange":
      return [
        ...state,
        {
          username: action.payload.username,
          password: action.payload.password,
          diet: "null",
          liked: [],
          id: uuidv4,
        },
      ];
    default:
      return state;
  }
};

export default usersReducer;
