import { Link } from 'react-router-dom';
import { useEffect, useRef, useState } from 'react';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft } from '@fortawesome/free-solid-svg-icons';

import classNames from 'classnames/bind';
import styles from './Login.module.scss';
const cx = classNames.bind(styles);

function Login() {
    const [user, setUser] = useState({
        username: '',
        password: '',
    });

    const handleChangeUsername = (e) => {
        setUser({ ...user, username: e.target.value });
    };

    const handleChangePassword = (e) => {
        setUser({ ...user, password: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (user.username === '' || user.password === '') {
            return;
        }

        const userLogin = users.find((userFetch) => {
            return userFetch.username === user.username && userFetch.password === user.password;
        });

        if (!userLogin) {
            setUser({
                username: '',
                password: '',
            });
            usernameRef.current.focus();
        } else {
            window.location.href = '/';
        }
    };

    const hanldeToHome = () => {};

    const usernameRef = useRef();

    const [users, setUsers] = useState([]);

    // Fetch users
    useEffect(() => {
        fetch('http://localhost:3000/users')
            .then((res) => res.json())
            .then((data) => setUsers(data));
    }, []);

    return (
        <div className={cx('wrapper')}>
            <div className={cx('fadeIn first')}>
                <img
                    className={cx('header')}
                    src="https://media.wired.co.uk/photos/626c4ae5c7aeb6a48f3c9c68/3:2/w_2025,h_1350,c_limit/Spirited-Away-Studio-Ghibli-Ranking-MMDSPAW_EC001.jpg"
                    alt=""
                />
            </div>

            <h4 className={cx('label')}>
                <Link to="/">
                    <FontAwesomeIcon className={cx('back-icon')} icon={faChevronLeft} />
                </Link>
                Đăng nhập
            </h4>

            <form className={cx('form-template')}>
                <input
                    className={cx('fadeIn second')}
                    ref={usernameRef}
                    type="text"
                    placeholder="Tên đăng nhập"
                    value={user.username}
                    onChange={handleChangeUsername}
                />
                <input
                    className={cx('fadeIn third')}
                    type="password"
                    placeholder="Mật khẩu"
                    value={user.password}
                    onChange={handleChangePassword}
                />
                <input type="submit" className={cx('fadeIn fourth')} value="Đăng nhập" onClick={handleSubmit} />
            </form>

            <Link className={cx('forgot-password')} to="/forgotPassword">
                Quên mật khẩu?
            </Link>

            <div className={cx('formFooter')}>
                Chưa có tài khoản?
                <Link to="/register" className={cx('underlineHover')}>
                    Đăng ký
                </Link>
                ngay
            </div>
        </div>
    );
}

export default Login;
