import classNames from 'classnames/bind';
import styles from './Forgot.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowLeft, faBackward, faChevronLeft } from '@fortawesome/free-solid-svg-icons';
import { Link } from 'react-router-dom';

const cx = classNames.bind(styles);

function ForgotPassword() {
    return (
        <div class={cx('wrapper')}>
            <div class={cx('fadeIn first')}>
                <img
                    className={cx('header')}
                    src="https://media.wired.co.uk/photos/626c4ae5c7aeb6a48f3c9c68/3:2/w_2025,h_1350,c_limit/Spirited-Away-Studio-Ghibli-Ranking-MMDSPAW_EC001.jpg"
                    id="icon"
                    alt="User Icon"
                />
            </div>

            <h4>
                <Link to="/login">
                    <FontAwesomeIcon className={cx('back-icon')} icon={faChevronLeft} />
                </Link>
                Lấy lại mật khẩu
            </h4>

            <form>
                <input
                    type="text"
                    id="ForgotPassword"
                    class={cx('fadeIn second')}
                    name="ForgotPassword"
                    placeholder="Tên đăng nhập"
                />
                <input
                    type="password"
                    id="password"
                    class={cx('fadeIn third')}
                    name="ForgotPassword"
                    placeholder="Mật khẩu mới"
                />
                <input
                    type="password"
                    id="password"
                    class={cx('fadeIn third')}
                    name="ForgotPassword"
                    placeholder="Xác nhận mật khẩu"
                />
                <input type="submit" class={cx('fadeIn fourth')} value="Xác nhận" />
            </form>

            <div className={cx('formFooter')}></div>
        </div>
    );
}

export default ForgotPassword;
