const initState = {
  isLoggedIn: false,
  username: "",
  password: "",
  diet: null,
  liked: [],
  id: null,
};

const userReducer = (state = initState, action) => {
  switch (action.type) {
    case "user/login":
      return {
        ...action.payload,
        isLoggedIn: true,
      };
    case "user/logout":
      return {
        isLoggedIn: false,
        username: "",
        password: "",
        diet: null,
        liked: [],
        id: null,
      };
    default:
      return state;
  }
};

export default userReducer;
