// const store = createStore(rootReducer, composeEnhancers);

// export default store;

import { configureStore } from "@reduxjs/toolkit";
import userReducer from "~/components/GlobalStyle/userSlice";
import filtersReducer from "~/components/Header/filtersSlice";
import foodsReducer from "~/components/Main/foodsSlice";
import usersReducer from "~/components/pages/forms/usersSlice";

const store = configureStore({
  reducer: {
    filters: filtersReducer,
    foods: foodsReducer,
    users: usersReducer,
    user: userReducer,
  },
});

export default store;
