import {
  FETCH_USERS_REQUEST,
  FETCH_USERS_SUCESS,
  FETCH_USERS_ERROR,
  UPDATE_USERS_REQUEST,
  UPDATE_USERS_SUCESS,
  UPDATE_USERS_ERROR,
} from "../constants/usersConstants";

export const loadUsers = () => async (dispath) => {
  try {
    dispath({ type: FETCH_USERS_REQUEST });

    const url = "http://localhost:3000/users";
    const res = await fetch(url);
    const data = await res.json();

    dispath({ type: FETCH_USERS_SUCESS, data });
  } catch (err) {
    dispath({ type: FETCH_USERS_ERROR, message: err });
  }
};

export const updateUser = (id, data) => async (dispath) => {
  try {
    dispath({ type: UPDATE_USERS_REQUEST });

    const url = "http://localhost:3000/users";
    const userId = id;
    const updateData = data;
    const res = await fetch(url + "/" + userId, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updateData),
    });
    const resJson = await res.json();

    dispath({ type: UPDATE_USERS_SUCESS, resJson });
  } catch (err) {
    dispath({ type: UPDATE_USERS_ERROR, message: err });
  }
};
