import { combineReducers } from "redux";

import filtersReducer from "~/components/Header/filtersSlice";
import foodsReducer from "~/components/Main/foodsSlice";

const rootReducer = (state = {}, action) => ({
  filters: filtersReducer(state.filters, action),
  foods: foodsReducer(state.foods, action),
});

export default rootReducer;
