import { Header } from '~/components/Header';

import classNames from 'classnames/bind';
import styles from './FoodDetails.module.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
const cx = classNames.bind(styles);

function FoodDetails({ food, onClick }) {
    return (
        <div className={cx('wrapper')} onClick={onClick}>
            <img
                className={cx('food-img')}
                src="https://cdn.tgdd.vn/Files/2020/06/24/1265202/3-cach-lam-kem-dua-luoi-ngon-ngot-thanh-mat-xua-d-760x367.jpg"
                alt=""
            />
            <div className={cx('header')}>
                <h1 className={cx('food-name')}>{food.name}</h1>
                <div className={cx('food-type')}>
                    <p>Chế độ ăn: ăn chay</p>
                </div>
                <div className={cx('food-like')}>
                    <p>{food.heart} người thích</p>
                    <FontAwesomeIcon icon={faHeart} className={cx('icon')} />
                </div>
            </div>
            <div className={cx('food-info')}>
                {food.info.map((item, index) => (
                    <p key={index}>
                        Bước {index} : {item}
                    </p>
                ))}
            </div>

            <div className={cx('food-list-block')}>
                <h4>Nguyên liệu</h4>
                <ul className={cx('food-list')}>
                    {food.list.map((item, index) => (
                        <li key={index} className={cx('food-list-item')}>
                            {item}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default FoodDetails;
