import { FETCH_POST_ERROR, FETCH_POST_REQUEST, FETCH_POST_SUCESS } from '../constants/postConstans';

const initialState = {
    requesting: false,
    sucess: false,
    message: null,
    data: null,
};

function postReducer(state = initialState, payload) {
    switch (payload.type) {
        case FETCH_POST_REQUEST:
            return {
                ...state,
                requesting: true,
            };
        case FETCH_POST_SUCESS:
            return {
                ...state,
                requesting: false,
                sucess: true,
                data: payload.data,
            };
        case FETCH_POST_ERROR:
            return {
                ...state,
                requesting: false,
                sucess: false,
                message: payload.message,
            };
        default:
            return state;
    }
}

export default postReducer;
