const initState = {
  name: "",
  ingredients: [],
  diet: "",
};

const filtersReducer = (state = initState, action) => {
  switch (action.type) {
    case "filters/searchNameChange":
      return {
        ...state,
        name: action.payload,
      };
    default:
      return state;
  }
};

export default filtersReducer;
