import {
  FETCH_USERS_REQUEST,
  FETCH_USERS_SUCESS,
  FETCH_USERS_ERROR,
} from "../constants/usersConstants";

const initialState = {
  requesting: false,
  sucess: false,
  message: null,
  data: null,
};

function usersReducer(state = initialState, payload) {
  switch (payload.type) {
    case FETCH_USERS_REQUEST:
      return {
        ...state,
        requesting: true,
      };
    case FETCH_USERS_SUCESS:
      return {
        ...state,
        requesting: false,
        sucess: true,
        data: payload.data,
      };
    case FETCH_USERS_ERROR:
      return {
        ...state,
        requesting: false,
        sucess: false,
        message: payload.message,
      };
    default:
      return state;
  }
}

export default usersReducer;
