import {
  FETCH_USERS_REQUEST,
  FETCH_USERS_SUCESS,
  FETCH_USERS_ERROR,
  UPDATE_USERS_REQUEST,
  UPDATE_USERS_SUCESS,
  UPDATE_USERS_ERROR,
  CREATE_USERS_REQUEST,
  CREATE_USERS_SUCESS,
  CREATE_USERS_ERROR,
  ADD_LIKE_REQUEST,
  ADD_LIKE_SUCESS,
  ADD_LIKE_ERROR,
  REMOVE_LIKE_REQUEST,
  REMOVE_LIKE_SUCESS,
  REMOVE_LIKE_ERROR,
} from "../constants/usersConstants";

const initialState = {
  requesting: false,
  sucess: false,
  message: null,
  data: null,
};

function usersReducer(state = initialState, payload) {
  switch (payload.type) {
    case FETCH_USERS_REQUEST:
      return {
        ...state,
        requesting: true,
      };
    case FETCH_USERS_SUCESS:
      return {
        ...state,
        requesting: false,
        sucess: true,
        data: payload.data,
      };
    case FETCH_USERS_ERROR:
      return {
        ...state,
        requesting: false,
        sucess: false,
        message: payload.message,
      };
    default:
      return state;
  }
}

export default usersReducer;
