import { Routes, Route } from "react-router-dom";

import { Home } from "./components/pages/Home";
import { Login } from "./components/pages/forms/Login";
import { Register } from "./components/pages/forms/Register";
import { Forgot } from "./components/pages/forms/Forgot";

import { useDispatch, useSelector } from "react-redux";
import { loadPost } from "./redux/actions/postActions";
import { useEffect } from "react";

function App() {
  const foods = [
    {
      id: 1,
      name: "Kem dừa mã lai",
      list: ["sữa", "sữa chua", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Làm đông 30p", "Rắc dừa khô"],
      heart: 30,
    },
    {
      id: 2,
      name: "Kem matcha mã lai",
      list: ["sữa", "bột matcha", "dừa", "dừa khô"],
      info: [
        "Xay sữa dừa matcha",
        "Trộn bột matcha",
        "Làm đông 30p",
        "Rắc dừa khô",
      ],
      heart: 40,
    },
    {
      id: 3,
      name: "Kem xoài mã lai",
      list: ["sữa", "xoài", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Xay xoài", "Làm đông 30p", "Rắc dừa khô"],
      heart: 60,
    },
    {
      id: 4,
      name: "Kem bơ mã lai",
      list: ["sữa", "bơ", "dừa", "dừa khô"],
      info: ["Xay sữa dừa", "Xay bơ", "Làm đông 30p", "Rắc dừa khô"],
      heart: 50,
    },
  ];
  const users = [
    {
      id: 1,
      username: "anchay",
      password: "123456",
      diet: null,
    },
    {
      id: 2,
      username: "toitenho",
      password: "123456",
      diet: null,
    },
    {
      id: 3,
      username: "ranhmanh",
      password: "123456",
      diet: null,
    },
  ];

  sessionStorage.setItem("users", JSON.stringify(users));
  sessionStorage.setItem("foods", JSON.stringify(foods));

  const dispath = useDispatch();
  const post = useSelector((state) => state.post);

  useEffect(() => {
    dispath(loadPost());
  }, []);

  useEffect(() => {
    console.log(post);
  }, [post]);
  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Home />} />
        {/* <Route path="/food/:id" element={<FoodDetails />} /> */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/forgotPassword" element={<Forgot />} />
      </Routes>
    </div>
  );
}

export default App;
