import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowUpRightFromSquare,
  faArrowsToCircle,
  faHeart,
} from "@fortawesome/free-solid-svg-icons";

import classNames from "classnames/bind";
import styles from "./FoodCard.module.scss";
import { useState } from "react";
import { userSelector } from "~/redux/selectors";
import { useDispatch, useSelector } from "react-redux";
import { toggleFoodLike } from "../foodsSlice";
import { toggleUserLike } from "~/components/pages/forms/usersSlice";

const cx = classNames.bind(styles);

function FoodCard({ food }) {
  const dispatch = useDispatch();
  const user = useSelector(userSelector);
  const [like, setLike] = useState(
    food.userLiked.includes(user.id) ? "like" : "no-like"
  );

  const [expand, setExpand] = useState("disexpand");

  const hanldeToggleLike = () => {
    if (!user.isLoggedIn) {
      alert("Ban phai dang nhap de like");
      return;
    }
    if (like === "like") {
      setLike("no-like");
      dispatch(
        toggleFoodLike({
          ...food,
          userLiked: food.userLiked.filter((userId) => userId !== user.id),
        })
      );

      dispatch(
        toggleUserLike({
          id: user.id,
          username: user.username,
          password: user.password,
          diet: user.diet,
          liked: user.liked.filter((foodId) => foodId !== food.id),
        })
      );
    } else {
      setLike("like");
      dispatch(
        toggleFoodLike({
          ...food,
          userLiked: [...food.userLiked, user.id],
        })
      );
      dispatch(
        toggleUserLike({
          id: user.id,
          username: user.username,
          password: user.password,
          diet: user.diet,
          liked: [...user.liked, food.id],
        })
      );
    }
  };

  return (
    <div className={cx("wrapper", expand)}>
      <img
        className={cx("food-img")}
        src="https://cdn.tgdd.vn/Files/2020/06/24/1265202/3-cach-lam-kem-dua-luoi-ngon-ngot-thanh-mat-xua-d-760x367.jpg"
        alt=""
      />
      <div className={cx("overlay")}>
        <div className={cx("container")}>
          <h4 className={cx("diet")}>{food.diet}</h4>
          <h1 className={cx("name")}>{food.name}</h1>
          <p className={cx("food-like")}>
            <span>・</span>
            {food.userLiked.length} lượt thích
          </p>

          <button>Xem thêm</button>
        </div>
      </div>
      {/* <div className={cx("recipe")}>
        {food.recipe.map((step, index) => (
          <p key={index}>
            Bước {index + 1} : {step}
          </p>
        ))}
      </div>

      <div className={cx("ingredients-block")}>
        <h4>Nguyên liệu</h4>
        <ul className={cx("ingredients")}>
          {food.ingredients.map((ingredient, index) => (
            <li key={index} className={cx("ingredient-block")}>
              <p className={cx("ingredient-item", "item-name")}>
                {ingredient.name}:
              </p>
              <p className={cx("ingredient-item")}>{ingredient.quantity}</p>
              <p className={cx("ingredient-item")}>{ingredient.unit}</p>
            </li>
          ))}
        </ul>
      </div> */}
    </div>
  );
}

export default FoodCard;
