import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faArrowUpRightFromSquare,
  faArrowsToCircle,
  faHeart,
} from "@fortawesome/free-solid-svg-icons";

import classNames from "classnames/bind";
import styles from "./FoodCard.module.scss";
import { useEffect, useState } from "react";

const cx = classNames.bind(styles);

function FoodCard({ id, name, recipe = [], ingredients = [], userLiked = [] }) {
  const [like, setLike] = useState("dislike");

  const [expand, setExpand] = useState("disexpand");

  const hanldeToggleLike = () => {
    if (like === "dislike") {
      setLike("like");
    } else {
      setLike("dislike");
    }
  };

  const handleExpands = () => {
    setExpand(expand === "expand" ? "disexpand" : "expand");
  };

  return (
    <div className={cx("wrapper", expand)}>
      <img
        className={cx("food-img")}
        src="https://cdn.tgdd.vn/Files/2020/06/24/1265202/3-cach-lam-kem-dua-luoi-ngon-ngot-thanh-mat-xua-d-760x367.jpg"
        alt=""
      />
      <h4 className={cx("name")}>{name}</h4>
      <div className={cx("recipe")}>
        {recipe.map((step, index) => (
          <p key={index}>
            Bước {index + 1} : {step}
          </p>
        ))}
      </div>

      <div className={cx("ingredients-block")}>
        <h4>Nguyên liệu</h4>
        <ul className={cx("ingredients")}>
          {ingredients.map((ingredient, index) => (
            <li key={index} className={cx("ingredient-block")}>
              <p className={cx("ingredient-item", "item-name")}>
                {ingredient.name}:
              </p>
              <p className={cx("ingredient-item")}>{ingredient.quantity}</p>
              <p className={cx("ingredient-item")}>{ingredient.unit}</p>
            </li>
          ))}
        </ul>
      </div>
      <div className={cx("food-feedback")}>
        <div className={cx("food-like")}>
          <p>{userLiked.length}</p>
          <FontAwesomeIcon
            icon={faHeart}
            className={cx("icon", `${like}`)}
            onClick={hanldeToggleLike}
          />
        </div>
        <FontAwesomeIcon
          icon={
            expand !== "expand" ? faArrowUpRightFromSquare : faArrowsToCircle
          }
          className={cx("icon")}
          onClick={handleExpands}
        />
      </div>
    </div>
  );
}

export default FoodCard;
