import classNames from 'classnames/bind';
import styles from './FilterByType.module.scss';
import { useState } from 'react';
const cx = classNames.bind(styles);

function FilterByType() {
    const [chooseMode, setChooseMode] = useState(false);

    const handleToggleSelectMode = () => {
        setChooseMode(!chooseMode);
    };
    return (
        <div className={cx('wrapper')}>
            <div className={cx('item', 'selected')} onClick={handleToggleSelectMode}>
                Chế độ ăn
            </div>
            {chooseMode && (
                <div className={cx('item-list')}>
                    <div className={cx('item')}>Tất cả</div>
                    <div className={cx('item')}>Ăn kiêng</div>
                    <div className={cx('item')}>Ăn chay</div>
                    <div className={cx('item')}>Ăn mặn</div>
                </div>
            )}
        </div>
    );
}

export default FilterByType;
