  //   CREATE_FOODS_SUCESS,
  //   CREATE_FOODS_ERROR,
} from "../constants/foodsConstants";

export const loadFoods = () => async (dispath) => {
  try {
    dispath({ type: FETCH_FOODS_REQUEST });

    const url = "http://localhost:3000/foods";
    const res = await fetch(url);
    const data = await res.json();
    console.log("[data]", data);

    dispath({ type: FETCH_FOODS_SUCESS, data });
  } catch (err) {
    dispath({ type: FETCH_FOODS_ERROR, message: err });
  }
};

