import { FoodCard } from "./FoodCard";
import { useEffect, useState } from "react";

import classNames from "classnames/bind";
import styles from "./Main.module.scss";
import { FoodDetails } from "./FoodDetails";
const cx = classNames.bind(styles);

function Main({ foodSearch, render = false }) {
  const foods = JSON.parse(sessionStorage.getItem("foods"));
  // console.log(foods);

  const commonElements = foods.filter((food) =>
    food.list.filter((input) => foodSearch.inputs.includes(input))
  );

  const foodSearchList = foods.filter(
    (food) => food.name.includes(foodSearch.name) && 1
  );

  const [isShowDetails, setIsShowDetails] = useState(false);

  const handleShowDetails = (data) => {
    receiveFood(data);
    setIsShowDetails(!isShowDetails);
  };

  const handleHideDetails = () => {
    setIsShowDetails(!isShowDetails);
  };

  const [food, setFood] = useState({});

  const receiveFood = (data) => {
    setFood(data);
  };
  return (
    <div className={cx("wrapper")}>
      {!isShowDetails &&
        foods.map((food, index) => (
          <FoodCard
            key={index}
            name={food.name}
            list={food.list}
            info={food.info}
            heart={food.heart}
            onClick={() =>
              handleShowDetails({
                name: food.name,
                list: food.list,
                info: food.info,
                heart: food.heart,
              })
            }
          />
        ))}
      {isShowDetails && <FoodDetails onClick={handleHideDetails} food={food} />}
    </div>
  );
}

export default Main;
