import classNames from "classnames/bind";
import styles from "./FoodDetails.module.scss";
import { useLocation } from "react-router-dom";
import { Header } from "~/components/Header";
import { Sidebar } from "~/components/Sidebar";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";

const cx = classNames.bind(styles);

function FoodDetails() {
  const location = useLocation();
  const foodData = location.state?.foodData;

  const dispatch = useDispatch();
  const user = useSelector(userSelector);
  const [like, setLike] = useState(
    foodData.userLiked.includes(user.id) ? "like" : "no-like"
  );

  const hanldeToggleLike = () => {
    if (!user.isLoggedIn) {
      alert("Ban phai dang nhap de like");
      return;
    }
    if (like === "like") {
      setLike("no-like");
      dispatch(
        toggleFoodLike({
          ...food,
          userLiked: food.userLiked.filter((userId) => userId !== user.id),
        })
      );

      dispatch(
        toggleUserLike({
          id: user.id,
          username: user.username,
          password: user.password,
          diet: user.diet,
          liked: user.liked.filter((foodId) => foodId !== food.id),
        })
      );
    } else {
      setLike("like");
      dispatch(
        toggleFoodLike({
          ...food,
          userLiked: [...food.userLiked, user.id],
        })
      );
      dispatch(
        toggleUserLike({
          id: user.id,
          username: user.username,
          password: user.password,
          diet: user.diet,
          liked: [...user.liked, food.id],
        })
      );
    }
  };
  // console.log(foodData);
  return (
    <div className={cx("wrapper")}>
      <Header search={false} filter={false} />
      <Sidebar />
      <div className={cx("main")}>
        <div className={cx("content")}>
          <div className={cx("left")}>
            <img
              className={cx("food-img")}
              src="https://cdn.tgdd.vn/Files/2020/06/24/1265202/3-cach-lam-kem-dua-luoi-ngon-ngot-thanh-mat-xua-d-760x367.jpg"
              alt=""
            />
            <h1 className={cx("name")}>{foodData.name}</h1>
            <p className={cx("food-like")}>
              <FontAwesomeIcon icon={faHeart} onClick={hanldeToggleLike} />
              <span>・</span>
              {foodData.userLiked.length} lượt thích
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FoodDetails;
