import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faArrowUpRightFromSquare, faHeart } from '@fortawesome/free-solid-svg-icons';

import classNames from 'classnames/bind';
import styles from './FoodCard.module.scss';
import { useState } from 'react';
import { Link } from 'react-router-dom';
const cx = classNames.bind(styles);

function FoodCard({ name, heart, list = [], info = [] }) {
    const [like, setLike] = useState('like');

    const [isShowDetails, setIsShowDetails] = useState(false);

    const hanldeToggleLike = () => {
        setLike(like === 'like' ? 'dislike' : 'like');
    };

    const handleShowDetails = () => {
        setIsShowDetails(!isShowDetails);
    };
    return (
        <div className={cx('wrapper')}>
            <img
                className={cx('food-img')}
                src="https://cdn.tgdd.vn/Files/2020/06/24/1265202/3-cach-lam-kem-dua-luoi-ngon-ngot-thanh-mat-xua-d-760x367.jpg"
                alt=""
            />
            <h4 className={cx('food-name')}>{name}</h4>
            <div className={cx('food-info')}>
                {info.map((item, index) => (
                    <p key={index}>
                        Bước {index} : {item}
                    </p>
                ))}
            </div>

            <div className={cx('food-list-block')}>
                <h4>Nguyên liệu</h4>
                <ul className={cx('food-list')}>
                    {list.map((item, index) => (
                        <li key={index} className={cx('food-list-item')}>
                            {item}
                        </li>
                    ))}
                </ul>
            </div>
            <div className={cx('food-feedback')}>
                <div className={cx('food-like')}>
                    <p>{heart}</p>
                    <FontAwesomeIcon icon={faHeart} className={cx('icon', `${like}`)} onClick={hanldeToggleLike} />
                </div>
                <FontAwesomeIcon icon={faArrowUpRightFromSquare} className={cx('icon')} onClick={handleShowDetails} />
            </div>
        </div>
    );
}

export default FoodCard;
