import { createSelector } from "reselect";

export const foodsSelector = (state) => state.foods;

export const searchNameSelector = (state) => state.filters.name;

export const foodsRemainingSelector = createSelector(
  foodsSelector,
  searchNameSelector,
  (foods, name) => foods.filter((food) => food.name.includes(name))
);
