import classNames from "classnames/bind";
import styles from "./Forgot.module.scss";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronLeft } from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation } from "react-router-dom";

const cx = classNames.bind(styles);

function ForgotPassword() {
  const dispath = useDispatch();
  const location = useLocation();
  const { data } = location.state;
  useEffect(() => {
    console.log("[data]", data);
  }, []);

  const [user, setUser] = useState({
    username: "",
    newPassword: "",
    confirmPassword: "",
  });

  const handleChangeUsername = (e) => {
    setUser({ ...user, username: e.target.value });
  };

  const handleChangeNewPassword = (e) => {
    setUser({ ...user, newPassword: e.target.value });
  };

  const handleChangeConfirmPassword = (e) => {
    setUser({ ...user, confirmPassword: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (
      user.username === "" ||
      user.newPassword === "" ||
      user.confirmPassword === ""
    ) {
      return;
    }
    if (user.newPassword !== user.confirmPassword) {
      setUser({
        ...user,
        confirmPassword: "",
      });

      userConfirmPasswordRef.current.focus();
      return;
    }

    const userForgot = users.find((userFetch) => {
      return userFetch.username === user.username;
    });

    if (!userForgot) {
      setUser({
        username: "",
        newPassword: "",
        confirmPassword: "",
      });
      usernameRef.current.focus();
    } else {
      const newUsers = users.filter((user) => user.name !== userForgot.name);
      setUsers([
        ...newUsers,
        {
          username: user.username,
          password: user.newPassword,
          diet: null,
        },
      ]);
      window.location.href = "/login";
    }
  };

  const usernameRef = useRef();
  const userConfirmPasswordRef = useRef();

  const [users, setUsers] = useState([]);

  // Fetch users
  useEffect(() => {
    fetch("http://localhost:3000/users")
      .then((res) => res.json())
      .then((data) => setUsers(data));
  }, []);

  return (
    <div className={cx("wrapper")}>
      <div className={cx("fadeIn first")}>
        <img
          className={cx("header")}
          src="https://media.wired.co.uk/photos/626c4ae5c7aeb6a48f3c9c68/3:2/w_2025,h_1350,c_limit/Spirited-Away-Studio-Ghibli-Ranking-MMDSPAW_EC001.jpg"
          alt="User Icon"
        />
      </div>

      <h4 className={cx("label")}>
        <Link to="/login">
          <FontAwesomeIcon className={cx("back-icon")} icon={faChevronLeft} />
        </Link>
        Lấy lại mật khẩu
      </h4>

      <form className={cx("form-template")}>
        <input
          type="text"
          className={cx("fadeIn second")}
          placeholder="Tên đăng nhập"
          ref={usernameRef}
          value={user.username}
          onChange={handleChangeUsername}
        />
        <input
          type="password"
          className={cx("fadeIn third")}
          placeholder="Mật khẩu mới"
          value={user.newPassword}
          onChange={handleChangeNewPassword}
        />
        <input
          type="password"
          className={cx("fadeIn third")}
          placeholder="Xác nhận mật khẩu"
          ref={userConfirmPasswordRef}
          value={user.confirmPassword}
          onChange={handleChangeConfirmPassword}
        />
        <input
          type="submit"
          className={cx("fadeIn fourth")}
          value="Xác nhận"
          onClick={handleSubmit}
        />
      </form>

      <div className={cx("formFooter")}></div>
    </div>
  );
}

export default ForgotPassword;
