import {
  FETCH_FOODS_REQUEST,
  FETCH_FOODS_SUCESS,
  FETCH_FOODS_ERROR,
} from "../constants/foodsConstants";

const initialState = {
  requesting: false,
  sucess: false,
  message: null,
  data: null,
};

function foodsReducer(state = initialState, payload) {
  switch (payload.type) {
    case FETCH_FOODS_REQUEST:
      return {
        ...state,
        requesting: true,
      };
    case FETCH_FOODS_SUCESS:
      return {
        ...state,
        requesting: false,
        sucess: true,
        data: payload.data,
      };
    case FETCH_FOODS_ERROR:
      return {
        ...state,
        requesting: false,
        sucess: false,
        message: payload.message,
      };
    default:
      return state;
  }
}

export default foodsReducer;
