import { Link, useLocation, useNavigate } from "react-router-dom";
import { useRef, useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronLeft } from "@fortawesome/free-solid-svg-icons";
import { loadUsers, updateUser } from "~/redux/actions/usersAction";

import classNames from "classnames/bind";
import styles from "./Login.module.scss";
const cx = classNames.bind(styles);

function Login() {
  const [userInfo, setUserInfo] = useState({
    username: "",
    password: "",
  });
  const location = useLocation();
  if (location.state) {
    const { user, newPassword } = location.state;
    setUserInfo({ ...user, password: newPassword });
  }
  const dispath = useDispatch();
  const users = useSelector((state) => state.users.data);

  useEffect(() => {
    dispath(loadUsers());
  }, []);

  const handleChangeUsername = (e) => {
    setUserInfo({ ...userInfo, username: e.target.value });
  };

  const handleChangePassword = (e) => {
    setUserInfo({ ...userInfo, password: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (userInfo.username === "" || userInfo.password === "") {
      return;
    }

    const isValidInfo = users.find((user) => {
      return (
        userInfo.username === user.username &&
        userInfo.password === user.password
      );
    });

    if (!isValidInfo) {
      setUserInfo({
        username: "",
        password: "",
      });
      usernameRef.current.focus();
    } else {
      sessionStorage.setItem("isLoggedIn", true);
      window.location.href = "/";
    }
  };

  const navigate = useNavigate();

  const handleToForgot = () => {
    navigate("/forgotPassword", {
      state: {
        data: users,
      },
    });
  };

  const handleToRegister = () => {
    navigate("/register", {
      state: {
        data: users,
      },
    });
  };

  const usernameRef = useRef();

  return (
    <div className={cx("wrapper")}>
      <div className={cx("fadeIn first")}>
        <img
          className={cx("header")}
          src="https://media.wired.co.uk/photos/626c4ae5c7aeb6a48f3c9c68/3:2/w_2025,h_1350,c_limit/Spirited-Away-Studio-Ghibli-Ranking-MMDSPAW_EC001.jpg"
          alt=""
        />
      </div>

      <h4 className={cx("label")}>
        <Link to="/">
          <FontAwesomeIcon className={cx("back-icon")} icon={faChevronLeft} />
        </Link>
        Đăng nhập
      </h4>

      <form className={cx("form-template")}>
        <input
          type="text"
          placeholder="Tên đăng nhập"
          ref={usernameRef}
          value={userInfo.username}
          onChange={handleChangeUsername}
        />
        <input
          type="password"
          placeholder="Mật khẩu"
          value={userInfo.password}
          onChange={handleChangePassword}
        />
        <input type="submit" value="Đăng nhập" onClick={handleSubmit} />
      </form>

      <button className={cx("forgot-password", "btn")} onClick={handleToForgot}>
        Quên mật khẩu?
      </button>

      <div className={cx("formFooter")}>
        Chưa có tài khoản?
        <button
          className={cx("underlineHover", "register-btn", "btn")}
          onClick={handleToRegister}
        >
          Đăng ký
        </button>
        ngay
      </div>
    </div>
  );
}

export default Login;
