        <ul className={cx("ingredients")}>
          {food.ingredients.map((ingredient, index) => (
            <li key={index} className={cx("ingredient-block")}>
              <p className={cx("ingredient-item", "item-name")}>
                {ingredient.name}:
              </p>
              <p className={cx("ingredient-item")}>{ingredient.quantity}</p>
              <p className={cx("ingredient-item")}>{ingredient.unit}</p>
            </li>
          ))}
