import classNames from "classnames/bind";
import styles from "./FilterByType.module.scss";
import { useState } from "react";
import { useDispatch } from "react-redux";
import foodsSlice from "~/components/Main/foodsSlice";
const cx = classNames.bind(styles);

function FilterByType() {
  const dispatch = useDispatch();
  const [chooseMode, setChooseMode] = useState(false);

  const handleToggleSelectMode = () => {
    setChooseMode(!chooseMode);
  };

  const [diet, setDiet] = useState("");

  const handleSelectDiet = (e) => {
    setDiet(e.target.textContent);
    setChooseMode(!chooseMode);
    dispatch(foodsSlice.actions.searchDietChange(e.target.textContent));
  };

  return (
    <div className={cx("wrapper")}>
      <div className={cx("item", "selected")} onClick={handleToggleSelectMode}>
        {diet || "Chế độ ăn"}
      </div>
      {chooseMode && (
        <div className={cx("item-list")}>
          <div className={cx("item")} onClick={handleSelectDiet}>
            Tất cả chế độ ăn
          </div>
          <div className={cx("item")} onClick={handleSelectDiet}>
            Chế độ ăn thường
          </div>
          <div className={cx("item")} onClick={handleSelectDiet}>
            Chế độ ăn chay
          </div>
          <div className={cx("item")} onClick={handleSelectDiet}>
            Chế độ ăn kiêng
          </div>
        </div>
      )}
    </div>
  );
}

export default FilterByType;
