const initState = [
  {
    id: 1,
    name: "Kem dừa mã lai",
    recipe: ["ăng", "nhăng", "nhăng"],
    ingredients: [
      {
        name: "kem",
        quantity: 1,
        unit: "cân",
      },
      {
        name: "dừa",
        quantity: 2,
        unit: "quả",
      },
      {
        name: "mã lai",
        quantity: 3,
        unit: "gam",
      },
    ],
    userLiked: [4, 2],
  },
  {
    id: 2,
    name: "Kem tuyết dâu mã lai",
    recipe: ["ngon", "tuyệt", "zời"],
    ingredients: [
      {
        name: "kem",
        quantity: 1,
        unit: "cân",
      },
      {
        name: "dâu",
        quantity: 2,
        unit: "quả",
      },
      {
        name: "mã lai",
        quantity: 3,
        unit: "gam",
      },
    ],
    userLiked: [],
  },
  {
    id: 3,
    name: "Kem bơ mã lai",
    recipe: ["an", "nhang", "nhang"],
    ingredients: [
      {
        name: "kem",
        quantity: 1,
        unit: "cân",
      },
      {
        name: "bơ",
        quantity: 2,
        unit: "quả",
      },
      {
        name: "mã lai",
        quantity: 3,
        unit: "gam",
      },
    ],
    userLiked: [],
  },
  {
    id: 4,
    name: "Kem sữa chua hy lạp mã lai",
    recipe: ["an", "nhang", "nhang"],
    ingredients: [
      {
        name: "kem",
        quantity: 1,
        unit: "cân",
      },
      {
        name: "sữa đặc",
        quantity: 1,
        unit: "lon",
      },
      {
        name: "sữa chua",
        quantity: 2,
        unit: "hộp",
      },
      {
        name: "mã lai",
        quantity: 3,
        unit: "gam",
      },
      {
        name: "mã lai",
        quantity: 3,
        unit: "gam",
      },
    ],
    userLiked: [],
  },
];

const foodsReducer = (state = initState, action) => {
  switch (action.type) {
    case "foods/toggleFoodLikeChange":
      return state.map((food) => {
        if (food.id !== action.payload.foodId) {
          return food;
        } else if (!food.userLiked.includes(action.payload.userId)) {
          return {
            ...food,
            userLiked: [...food.userLiked, action.payload.userId],
          };
        } else {
          return {
            ...food,
            userLiked: food.userLiked.filter(
              (userId) => userId !== action.payload.userId
            ),
          };
        }
      });

    default:
      return state;
  }
};

export default foodsReducer;
